import React from 'react';
import ReactDOM from 'react-dom';

function tick() {
  const element = (
    <div>
      <h1>Hello, world!</h1>
      <h2>It is {new Date().toLocaleTimeString('pt', { hour12: true })}.</h2>
    </div>
  );

  function helloWorld (nome, sobrenome) {
    return <h1>Hello, {`${nome} ${sobrenome}`}</h1>;
  }

  const element2 = helloWorld("Jorge", "Maravilha");
  const container = <div>{element2}</div>;
  ReactDOM.render(element, container, document.getElementById('root'));
}

setInterval(tick, 1000);
